<?php/** * Plugin Name: Beaver Charts - Beaver Builder Chart Module * Plugin URI: https://wordpress.org/plugins/fd-beaver-charts/ * Description: Allow user to manipulate data via different chart presentations on your site with beaver builder add-on. * Version: 1.2.3 * Author: FlickDevs * Author URI: https://flickdevs.com * Text Domain: fd-beaver-charts */define('BEAVER_CHART_MODULE_DIR', plugin_dir_path(__FILE__));define('BEAVER_CHART_MODULE_URL', plugins_url('/', __FILE__));define('BCM_DOMAIN', 'fd-beaver-charts');require_once BEAVER_CHART_MODULE_DIR . 'classes/fd-beaver-chart-class.php';// Enqueue chart field assets.add_action('wp_enqueue_scripts', 'beaver_chart_script');function beaver_chart_script() {    wp_enqueue_script('beaver-chart-script', BEAVER_CHART_MODULE_URL . 'assets/js/chart.min.js', array(), true);}/** * Admin notices for activate  beaver builder plugin * */function beaver_chart_admin_notices() {    if (!is_admin()) {        return;    } else if (!is_user_logged_in()) {        return;    } else if (!current_user_can('update_core')) {        return;    }    if (!is_plugin_active('bb-plugin/fl-builder.php')) {        if (!is_plugin_active('beaver-builder-lite-version/fl-builder.php')) {            echo sprintf('<div class="notice notice-error"><p>%s</p></div>', __('The <b>Beaver Chart plugin requires </b> <a href="https://wordpress.org/plugins/beaver-builder-lite-version/" target="_blank">Beaver Builder Lite</a> or <a href="https://www.wpbeaverbuilder.com/pricing/" target="_blank">Beaver Builder Pro</a>.', BCM_DOMAIN));        }    }    if ($notices = get_option('beaver_chart_deferred_admin_notices')) {        foreach ($notices as $notice) {            echo "<div class='notice notice-success is-dismissible'><p>$notice</p></div>";        }        delete_option('beaver_chart_deferred_admin_notices');    }}add_action('admin_notices', 'beaver_chart_admin_notices');/** * Admin notices on activate  beaver chart plugin for give customer reviews * */register_activation_hook(__FILE__, 'beaver_chart_plugin_activation');function beaver_chart_plugin_activation() {    $notices = get_option('beaver_chart_deferred_admin_notices', array());    $notices[] = '<p>Hi, you are now using <b>Beaver Chart</b> plugin. I would really appreciate it if you could give me the five star to our plugin. </p><p><a href="https://wordpress.org/plugins/fd-beaver-charts/advanced/" target="_blank" class="rating-link"><strong> Yes, you deserv it </strong></a></p>';    update_option('beaver_chart_deferred_admin_notices', $notices);}register_deactivation_hook(__FILE__, 'beaver_chart_plugin_deactivation');function beaver_chart_plugin_deactivation() {    delete_option('beaver_chart_deferred_admin_notices');}