(function($) {    var densityCanvas = document.getElementById("fl-barchart-<?php echo $id; ?>");    var barChart = new Chart(densityCanvas, {        type: 'bar',        data: {            labels: [<?php echo $settings->xaxis_data_value;  ?>],            datasets: [ <?php 		        foreach( $settings->fl_bar_chart as $barchart_value ){ ?>				{                    label: '<?php echo $barchart_value->bar_tag_text;  ?>',                    data: [<?php echo $barchart_value->yaxis_data_value;  ?>],		            backgroundColor:'#<?php echo $barchart_value->bar_color;  ?>',					borderWidth: '<?php echo $settings->bar_border_width;  ?>',					borderColor:'#<?php echo $settings->bar_border_color;  ?>'				    				},<?php } ?>			]    },	options:{				responsive: true,		<?php if($settings->bar_chart_tag =='yes' ){ ?>		legend: {                    display: true,					position:'<?php echo $settings->bar_tag_position;  ?>',					labels: {												fontSize: <?php echo $settings->bar_tag_fontsize;  ?>,												fontColor: '#<?php echo $settings->bar_tag_fontcolor;  ?>'					},		},		 <?php } else { ?>		 legend: {                    display: false,		},		 <?php }	?>	 		 scales: {			xAxes: [{				stacked: false,				display: true,				position: '<?php echo $settings->xaxis_line_position; ?>',                scaleLabel: {					display: <?php echo $settings->xaxis_label_display; ?>,					labelString: '<?php echo $settings->xaxis_label; ?>',                    fontSize: <?php echo $settings->xaxis_label_fontsize; ?>,					fontColor: '#<?php echo $settings->xaxis_label_fontcolor;  ?>'			  },								gridLines: {					display: <?php echo $settings->xaxis_gridline; ?>			  },			    ticks: {										fontSize: 14,					<?php if($settings->xaxis_callback_position == 'prefix') {?>					callback: function(value, index, values) {											   return '<?php echo $settings->bar_xaxis_callback; ?>' + value;					}